import { useEffect, useReducer, useState } from 'react';
import './Table.css';
import Item from '../Item/Item'
import getItems from '../../utils/getItems';

function Table() {
  //получаем список пользователей
  const initialState = { items: [], total: 0 }
  const [items, setItems] = useState({initialState})
  const [state, dispatch] = useReducer(initialState);

  // запрос списка
  useEffect(() => {
    getItems()
      .then((result) => {
        const items = result.items
        dispatch({ type: 'initial', items })
      })
      .catch(e => console.log(e))
  }, [])
  return (
    <div className="table">
      <table className="table_unit">
        <thead>
          <tr>
            <th className="table_th">Имя пользователя</th>
            <th className="table_th">E-mail</th>
            <th className="table_th">Дата регистрации</th>
            <th className="table_th">Рейтинг</th>
            <th className="table_th"></th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => Item({ item }))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
