import { useEffect, useState } from 'react';
import './Pagination.css'


function Pagination({ itemsAmount }) {
  //пагинация
  const [currentPage, setCurrentPage] = useState(1);
  //задаем количество записей на странице
  const [pageList] = useState(5);
  //индекс последней записи на странице
  const lastNote = currentPage * pageList
  //индекс первой записи на странице
  const firstNote = lastNote - pageList
  //индекс текущей записи
  //const currentNote = items.slice(firstNote, lastNote)


  const pagesAmount = []
  for (let i = 1; i <= Math.ceil(25 / 5); i++) {
    pagesAmount.push(i)
  }
  return (
    <div className='pagination'>
      <ul className='pagination_list'>
        {
          pagesAmount.map(number => (
            <li className='pagination__page' key={number}>
              <a href="#" className='pagination__button'>{number}</a>
            </li>
          ))
        }
      </ul>
    </div>
  )
}

export default Pagination
