import { useEffect, useState } from 'react';
import './Table.css';
import Item from '../Item/Item'
import getItems from '../../utils/getItems';


const DATE = 'registration_date'
const RATING = 'rating'

function Table() {
  //получаем список пользователей
  const [items, setItems] = useState([])
  const [sortedColomn, setSortedColomn] = useState('')

  // запрос списка
  useEffect(() => {
    getItems()
      .then((result) => {
        setItems(result)
      })
      .catch(e => console.log(e))
  }, [])

  function sortBy(column) {
    switch (column) {
      case DATE: 
        setItems([...items].sort((a, b) => {
          return new Date(a[DATE]) - new Date(b[DATE])
        }))
        break
      case RATING: 
        setItems([...items].sort((a, b) => a[RATING] - b[RATING]))
        break
      default: 
      setItems([...items].sort((a, b) => a - b))
    }
    setSortedColomn(column)
  }

  return (
    <>
      <div>
        <span className="table__sort">Сортировка:</span>
        <span className={'table__reg'} onClick={() => sortBy(DATE)}>Дата регистрации</span>
        <span className={'table__rate' + sortedColomn === RATING ? ' active' : ''}  onClick={() => sortBy(RATING)}>Рейтинг</span>
      </div>

      <div className="table">
        <table className="table__unit">
          <thead>
            <tr>
              <th className="table__th">Имя пользователя</th>
              <th className="table__th">E-mail</th>
              <th className="table__th">Дата регистрации</th>
              <th className="table__th">Рейтинг</th>
              <th className="table__th"></th>
            </tr>
          </thead>
          <tbody>
            {items.map(item => Item({ item }))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Table;
